import React, { Component } from 'react';
import './graphPage.css';
import 'bootstrap/dist/css/bootstrap.css';
import Subcategory from './menus/Subcategory.js';
import Location2 from './menus/Location2.js';
import Date from './menus/Date.js'
import Demographics from './menus/Demographic.js';
import firebase from 'firebase';
import 'firebase/storage';
import firebaseConfig from '../src/Firebase/firebase.js';
import { SphereSpinner } from "react-spinners-kit";
import LineChart from './LineChart.js';

firebase.initializeApp(firebaseConfig);

class GraphPage extends Component {
  constructor(props) {
    super(props)
    this.state = {
      location: 'All States',
      allStates: null,
      data: null,
      cases: {},
    }
    this.updateLocation = this.updateLocation1
}
updateLocation1 = (state) => {this.setState({ location: state })}

componentDidMount() {
  const storage = firebase.storage();
  const storageRef = storage.ref();    
  storageRef.child('data/allStates.json').getDownloadURL().then(url => {
    this.makeRequest(url)
  });


}

makeRequest = (url) => {
    var xhr = new XMLHttpRequest();
    xhr.responseType = 'json';
    xhr.onload = function(e){
      if (xhr.readyState === 4){
        if (xhr.status === 200){
          var data = xhr.response
        } else {
          console.error(xhr.statusText)
        }
      }
    }
    xhr.onreadystatechange = () => {
      var data = xhr.response;
      if (data != null) {
        for( let i = 0; i < data.length; i++) {
          var dates = data[i].Date.split(',');
          var date = dates[0].substring(0, 3) + ' ' + dates[1] + ' ' + dates[2] + ' 00:00:00' + ' GMT-0500 (Eastern Standard Time)';
          data[i].Date = date;
        }
        this.setState({
          allStates: data,
          data: data,
        })
        this.updateDates();
      } 

    }
    xhr.open('GET', url);
    xhr.send();

}

updateDates = () => {
  const {allStates} = this.state;
  for( let i = 0; i < allStates.length; i++) {
    var date = allStates[i].Date
    let newDate = new Date('Sun Jan 01 2017 00:00:00 GMT-0500 (Eastern Standard Time)');
    console.log(date);
  }

}

  render() {
    console.log(this.state);
    const { location, allStates, data }  = this.state;

    return (
      <div className="graphPage">
        <img className="logo" src={require('./wonder-logo.png')} />
        <img className="home" src={require('./home.png')} />
        <h1>Cancer</h1>
        <h2>{location}</h2>
        <Subcategory />
        <Location2 updateLocation = {this.updateLocation1}/>
        <Date />
        <Demographics />
        { data == null ? <div className="loader"><SphereSpinner 
                size={50}
                color="#2980b9"
                loading={true}
            /></div> : <div>Loaaded</div>}
        <LineChart data={data} />
      </div>
    );


  }

}

export default GraphPage;
